function Options({ question, dispatch, answer }) {
	return (
		<div className="options">
			{question.options.map((options, index) => (
				<button
					className="btn btn-option"
					key={options}
					onClick={() => dispatch({ type: "newAnswer", payload: index })}
				>
					{options}
				</button>
			))}
		</div>
	);
}

export default Options;
